sys_script_include.script:
var GetEmailAddress_2 = Class.create();
GetEmailAddress_2.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getEmail: function() {
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('sysparm_userID'));
		return userRecord.email + '';
	},

	getFristName: function() {
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('sysparm_userID'));
		return userRecord.first_name + '';
	},

	getLastName: function() {
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('sysparm_userID'));
		return userRecord.last_name + '';
	},
	getUserId: function() {
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('sysparm_userID'));
		return userRecord.user_name + '';
	},

	getRecord: function() {
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('sysparm_userID'));

		return userRecord.last_name+'-'+ userRecord.first_name + '-'+userRecord.user_name+' ' ;
	},

    type: 'GetEmailAddress_2'
});
